mapper.select:
select
  id, name, description, url, requestParams, requestHeader, responseHeader, status, method, userId, createTime, updateTime, isDelete
from interface_info


        and id = #{id}


        and name = #{name}


        and description = #{description}


        and url = #{url}


        and requestParams = #{requestparams}


        and requestHeader = #{requestheader}


        and responseHeader = #{responseheader}


        and status = #{status}


        and method = #{method}


        and userId = #{userid}


        and createTime = #{createtime}


        and updateTime = #{updatetime}


        and isDelete = #{isdelete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from interface_info


        and id = #{id}


        and name = #{name}


        and description = #{description}


        and url = #{url}


        and requestParams = #{requestparams}


        and requestHeader = #{requestheader}


        and responseHeader = #{responseheader}


        and status = #{status}


        and method = #{method}


        and userId = #{userid}


        and createTime = #{createtime}


        and updateTime = #{updatetime}


        and isDelete = #{isdelete}
